www.youtube.com/embed/'+match[2]+'?autoplay=0&enablejsapi=1"></iframe>');			$("#video_success").html("Video Url saved successfully!!");		}		else    		{			$("#video_success").html("");			$("#videoObject").html("");			$("#video_error").html("Please provide an valid YouTube Video Url!!");			return false;		}	}	else	{		SaveUrl("",<?php echo $listDetail->row()->id; ?>,'video_url');		$("#videoObject").html("");		$("#video_success").html("");		$("#video_error").html("");	}}function SaveUrl(url,catID,chk){	var title = url;		var msg=$("#saved").val();		$.ajax({			type:'post',			url:baseURL+'site/product/saveDetailPage',			data:{'catID':catID,'title':title,'chk':chk},				complete:function(){				$('#imgmsg_'+catID).hide();				//$('#imgmsg_'+catID).show().text('Saved');				$('#imgmsg_'+catID).show().text(msg);			}		});}</script><?php$this->load->view('site/templates/footer');?>