 VALUES("21","Payment Authorization","Both","Active","en","21","payment-authorization");INSERT INTO fc_help_main VALUES("22","Booking a Property /Space","Guest","Active","en","22","booking-a-property-or-space");INSERT INTO fc_help_main VALUES("23","Your Profile","Both","Active","en","23","your-profile");INSERT INTO fc_help_main VALUES("24","Searching for a Property/Space","Guest","Active","en","24","searching-for-a-property-space");INSERT INTO fc_help_main VALUES("25","Guest Review","Guest","Active","en","25","guest-review");INSERT INTO fc_help_main VALUES("26","Hosting","Host","Active","en","26","hosting");INSERT INTO fc_help_main VALUES("27","Traveling","Guest","Active","en","27","traveling");INSERT INTO fc_help_main VALUES("29","Invite Friends","Both","Active","en","29","invite-friends");CREATE TABLE `fc_help_question` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
